mapper.delete:
update checkfeedback
set deleteFlag=1
where guid = #{guid}

mapper.select:
SELECT t1.*, t2.`name` nodeName FROM checkfeedback t1 left join nodes t2 on t2.guid = t1.nodeGuid WHERE t1.deleteFlag = 0 AND t1.produceGuid = #{produceGuid}
and questionDescription like CONCAT ('%',#{questionDescription},'%')
and t1.status in
#{item}
and t1.projectGuid in
#{item}
order by t1.feedbackTime desc
